INSERT INTO tblLyrics VALUES("488", "Escape", "World get away from me\r\nI want you far away from me\r\nConfusion\\\'s gone, blackness reigns\r\nThere\\\'s no more hurt, no more pain\r\nLeave me alone \\\'cause I don\\\'t want to know\r\n\r\nGonna go in my room\r\nClose the door behind\r\nAlone world I\\\'m your loser\r\nBut you were wrong and so was I\r\nSo is everything\r\nSo is everybody\r\n\\\'Cause there\\\'s no point in this\r\nThere\\\'s no point in anything at all\r\nNo point in the void you call my soul\r\n\r\nDreams turned into nightmares\r\nDreams are dreams\r\nCrawl into blackness\r\nCome - scream into my mind", "27", "1", "1047030177");
