INSERT INTO tblLyrics VALUES("3082", "Waiting, hating", "I\'ve been waiting for five years\nNothin\' yet to belay my fears\nSometimes so unreal\nBut still searching for the truth\n\nI\'ve been waiting I\'ve been hating\nBut where can we go, it\'s so unreal\nI\'ve been waiting I\'ve been hating\nThey\'ll never change\n\nThere was a king there was a queen\nAnd when it added up it\'s all the same\nThey can love you with a look\nBut treason is the breath you took\n\nSit up and smile it\'s our acceptance\nThat breeds a lie I believe in joy\nAnd if this is not the answer\nThere\'s something I\'m still looking for", "137", "1", "1047030177");
